package basic_programs;

public class ArithmaticExamples {
	public static void main(String[] args) {
		// TODO Auto-generated method stub
//initialization
int num1=21;
int num2=10;
//declaration
int sum,sub,mul,mod;
float div;
//operation
sum = num1+num2;
sub = num1-num2;
mul = num1*num2;
div = num1/num2;
mod = num1%num2;
//Display
System.out.println("Sum is "+sum + " "  + "Sub is "+sub + " " + "Mul is "+mul + " " + "Div is "+div + " " + "Mod is "+mod);
	}

}
