package basic_programs;

public class UnaryOperator {
	public static void main(String[] args) {
		// TODO Auto-generated method stub
int num1=21;
int num2=21;
int num3=21;
int num4=21;
System.out.println("Post increment "+(num1++)+ " and after increment " +num1);
System.out.println("Pre increment "+(++num2)+ " and after increment " +num2);
System.out.println("Post decrement "+(num3--)+ " and after increment " +num3);
System.out.println("Pre decrement "+(--num4)+ " and after increment " +num4);
	}
}
